import dayjs from 'dayjs'
import { customAlphabet } from 'nanoid'
import { cloneDeep } from 'lodash-es'
import { eventTypes, operationTypes } from './eventTypes'
const nanoStr = '1234567890abcdefghijklmnopqrstuvwxyz'

export const dayjsTimeFormating = (time?: any) => {
  time = time || dayjs()
  return time.format('YYYY-MM-DD HH:mm:ss')
}

/**
 * 获取唯一id
 * @param len 长度
 * @returns
 */
export const getNanoid = (len?: number) => {
  len = len || 8
  return customAlphabet(nanoStr, len)(len)
}

// https://github.com/scopsy/await-to-js/blob/master/src/await-to-js.ts
/**
 * @param { Promise } promise
 * @param { Object= } errorExt - Additional Information you can pass to the err object
 * @return { Promise }
 */
export function awaitTo<T, U = Error>(promise: Promise<T>, errorExt?: object): Promise<[U, undefined] | [null, T]> {
  return promise
    .then<[null, T]>((data: T) => [null, data])
    .catch<[U, undefined]>((err: U) => {
      if (errorExt) {
        const parsedError = Object.assign({}, err, errorExt)
        return [parsedError, undefined]
      }

      return [err, undefined]
    })
}

/**
 * 单个对象属性变化，构建撤销重做数据(后续再考虑优化)
 * 构建后的数据形如 {activeSelection: null, data: [{index:0,data:{name: '123', locked: false}}]
 * @param toData 对象属性，形如：{name: 'GG', locked: false}
 * @param fromData 对象设置之前属性，形如：{name: '123', locked: true}
 */
export const constructSingleObjectHistoryData = (id: string, toData: any, fromData: any) => {
  const data: any = {
    to: {
      activeSelection: null,
      data: [{ data: { id, ...toData } }]
    }
  }
  if (fromData) {
    data.from = {
      activeSelection: null,
      data: [{ data: { id, ...fromData } }]
    }
  }
  return data
}

/**
 * 编辑器配置改变
 * @param keyList 改变的字段列表
 * @param editor 编辑器实例
 * @param editorConfig 编辑器配置
 * @param isSaveAction 是否保存到历史记录
 */
export const handleEditorConfigChange = (keyList: any = [], editor: any, editorConfig: any, isSaveAction = false) => {
  const { config } = editor
  const toConfig: any = {}
  const fromConfig: any = {}
  if (!editor.__tempLastConfig) {
    // __tempLastConfig保存上一次的配置，修改完之后，将__tempLastConfig删除
    editor.__tempLastConfig = {}
  }
  const lastConfig = editor.__tempLastConfig
  keyList.forEach((key: any) => {
    const item = Reflect.get(lastConfig, key)
    if (!item) {
      const cloneData = cloneDeep(Reflect.get(config, key))
      Reflect.set(lastConfig, key, cloneData)
    }
    const toData = cloneDeep(Reflect.get(editorConfig, key))
    Reflect.set(toConfig, key, toData)
    if (isSaveAction) {
      const cloneData = Reflect.get(lastConfig, key)
      Reflect.set(fromConfig, key, cloneData)
    }
  })
  const param = {
    to: toConfig
  }
  if (isSaveAction) {
    Reflect.set(param, 'from', fromConfig)
    Reflect.deleteProperty(editor, '__tempLastConfig')
  }
  editor.fire(eventTypes.coreOperation, { action: operationTypes.editorConfig, param })
}

/**
 * 恢复编辑器配置
 * @param keyList 要恢复的字段列表
 * @param editor 编辑器实例
 */
export const handleEditorConfigRecover = (keyList: any = [], editor: any) => {
  const lastConfig = editor.__tempLastConfig || {}
  const obj: any = {}
  keyList.forEach((key: any) => {
    const item = Reflect.get(lastConfig, key)
    Reflect.set(obj, key, item)
  })
  Reflect.deleteProperty(editor, '__tempLastConfig')
  const param = { to: obj }
  editor.fire(eventTypes.coreOperation, { action: operationTypes.editorConfig, param })
}

