/**
 * 校验文件是否为datavis模板文件
 * @param file 文件对象
 */
export const isValidDatavisFile = (file: File) => {
  return new Promise(resolve => {
    const reader = new FileReader()
    reader.readAsText(file, 'UTF-8')
    reader.onload = e => {
      const result: any = e.target?.result
      try {
        const data = JSON.parse(result)
        const hasScreenField = Reflect.has(data, 'screen')
        const hasBackendField = Reflect.has(data, 'backend')
        const hasFrontendField = Reflect.has(data, 'frontend')
        const params = {
          isValid: hasScreenField && hasBackendField && hasFrontendField,
          data
        }
        resolve(params)
      } catch (e) {
        resolve({ isValid: false })
      }
    }
  })
}

/**
 * 从树形结构中寻找对象
 * @param treeData 树形结构
 * @param childField 树形结构子级对象字段
 * @param callbackFun 回调函数，返回true时代表找到对象
 * @returns
 */
export const getObjFromTreeData = (treeData: Array<any>, childField: any, callbackFun: Function) => {
  let findObj: any
  const recursion = (arr: any) => {
    arr = arr || []
    if (findObj) {
      return
    }
    for (let i = 0; i < arr.length; i++) {
      if (findObj) {
        return
      }
      const item = arr[i]
      if (callbackFun(item)) {
        findObj = item
        return
      }
      recursion(item[childField])
    }
  }
  recursion(treeData)
  return findObj
}

/**
 * 获取滚动树的状态信息，包括滚动条位置和展开的节点信息
 * @param elTreeRef 树组件ref引用
 * @param scrollbarRef 滚动条组件ref引用
 * @returns
 */
export const getScrollTreeState = (elTreeRef: any, scrollbarRef: any) => {
  const { scrollTop } = scrollbarRef.value.wrapRef
  const expandedKeys: any = []
  Object.values(elTreeRef.value.store.nodesMap).forEach((item: any) => {
    if (item.expanded) {
      expandedKeys.push(item.data.id)
    }
  })
  return { scrollTop, expandedKeys }
}

/**
 * 设置滚动树的状态信息，包括滚动条位置和展开的节点信息
 * @param elTreeRef 树组件ref引用
 * @param scrollbarRef 滚动条组件ref引用
 * @param state 状态信息，包括滚动条位置和展开的节点信息
 */
export const setScrollTreeState = (elTreeRef: any, scrollbarRef: any, state: any) => {
  const { scrollTop, expandedKeys } = state
  scrollbarRef.value.setScrollTop(scrollTop)
  const { nodesMap } = elTreeRef.value.store
  Object.keys(nodesMap).forEach((key: any) => {
    const item = nodesMap[key]
    item.expanded = expandedKeys.includes(key)
  })
}
